import './Map.css';
import React, { Component } from 'react';
import {MapContainer, TileLayer, Marker, Popup} from 'react-leaflet'; 
import L from 'leaflet';

/*import L from 'leaflet';
  function GetIcon(_iconSize){
  return L.icon(options {
    iconUrl: require('../icons/location icons/treeicon1.png'),
    iconSize: [20]

  }
  );
*/

const iconPerson = new L.Icon({
  iconUrl: require('../icons/location icons/treeicon2.png'),
  iconRetinaUrl: require('../icons/location icons/treeicon2.png'),
  iconAnchor: null,
  popupAnchor: null,
  shadowUrl: null,
  shadowSize: null,
  shadowAnchor: null,
  iconSize: new L.Point(60, 75),
  className: 'leaflet-div-icon'
});

class Map extends Component {
  state = {
    lat: 53.551086,
    lng: 9.993682,
    zoom: 13,
  }



  //Standortabfrage//
  componentDidMount() {
      navigator.geolocation.getCurrentPosition((position)=> {
          console.log(position);
      });
  }
  //Standortabfrage//

  //Marker//

  //Marker//


  render() {
    const position = [this.state.lat, this.state.lng];

return (
  <MapContainer className="map" center={position} zoom={this.state.zoom} scrollWheelZoom={false}>
    <TileLayer
      url="https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png"
    />

    <Marker position={position} icon={iconPerson}>
    <Popup>TEST</Popup>


    </Marker>

    {/*<Marker position={position}>
      <Popup>
        Fischbeker Heide <br /> Plan your woodventure!.
      </Popup>
    </Marker>*/}

  </MapContainer>
  )
  }
}

export default Map;




