import './Map.css';
import React, { Component } from 'react';
import {MapContainer, TileLayer, Marker, Popup} from 'react-leaflet'; 
import L from 'leaflet';
import PopupContent from './PopupContent';


class Map extends Component {
  state = {
    lat: 53.551086,
    lng: 9.993682,
    zoom: 13,
  }



  //Standortabfrage//
  componentDidMount() {
      navigator.geolocation.getCurrentPosition((position)=> {
          console.log(position);
      });
  }
  //Standortabfrage//



  render() {
    const position = [this.state.lat, this.state.lng];
    const iconPerson = new L.Icon({
      iconUrl: '/icons/location-icons/location_icon_green.svg',
      iconAnchor: [0, 0],
      popupAnchor: [25, 0],
      iconSize: [50, 50]
    });

return (
  <MapContainer className="map" center={position} zoom={this.state.zoom} scrollWheelZoom={false}>
    <TileLayer
      url="https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png"
    />

    {this.props.forests.map(forest => (
      <Marker key="1" position={forest.position} icon={iconPerson}>
      <Popup className="popup__styling">
        <PopupContent forest={forest}/>
      </Popup>

    </Marker>
    )
    )
    }


  </MapContainer>
  )
  }
}

export default Map;




